import './App.css'
import Header from './Components/Header';
// import Sidebar from './components/Sidebar';
// import FeeSummary from './components/FeeSummary';
// import PaymentHistory from './components/PaymentsHistory';


function App() {
  return (
    <div className="d-flex flex-column vh-100">
      {/* Header */}
      <header className="header">
        <Header />
      </header>

      {/* Body */}
      <div className="d-flex flex-grow-1">
        {/* Left Sidebar */}
        <aside className="sidebar">
          <Sidebar />
        </aside>

        {/* Main content placeholder */}
        <main className="main-content flex-grow-1 p-2">
          {/* Fixed height section (20vh) spanning full width */}
          <div className="content-section-1 bg-success-subtle p-2 mb-1" style={{ minHeight: '25vh', maxHeight: '25vh', overflowY: 'auto' }}>
            <h3>Section 1 (20vh)</h3>
          </div>
          {/* Remaining space section */}
          <div className="content-section-2 d-flex flex-column bg-info-subtle" style={{ width: '64vw', maxHeight: 'calc(75vh - 8vh)', overflowY: 'auto' }}>
            {/* Annotation section */}
            <div className="annotation-section bg-danger-subtle p-2 mb-2" style={{ minHeight: '8vh', maxHeight: '8vh', overflowY: 'auto' }}>
              <h4>Annotation (5vh or 8vh)</h4>
            </div>
            {/* Remaining content of section 2 */}
            <div className="section-2-content flex-grow-1 bg-light p-2" style={{ minHeight: '0', overflowY: 'scroll', maxHeight: 'calc(60vh - 8vh)' }}>
              {/* <FeeSummary /> */}
              <PaymentHistory />
              <p>This is the remaining content of Section 2.</p>
            </div>
          </div>

          {/* Right aside */}
          <aside className="section-2-aside bg-warning-subtle p-2" style={{ minWidth: '20vw', maxWidth: '20vw', minHeight: 'calc(73vh - 8vh)', maxHeight: 'calc(75vh - 8vh)', overflowY: 'auto', position: 'absolute', right: 0, top: '34vh' }}>
            <h4>Right Sidebar (20vw)</h4>
          </aside>
        </main>
      </div>
    </div>
  );
}

export default App;
