import React from 'react';
import { Formik, Form, Field, ErrorMessage, FieldArray } from 'formik';
import {
  Box,
  TextField,
  Button,
  Typography,
  Container,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Grid,
} from '@mui/material';
import { Delete as DeleteIcon } from '@mui/icons-material';
import * as Yup from 'yup';

const ZoneApplication = () => {
  // Initial form values including an array for multiple zones
  const initialValues = {
    zones: [
      { zoneId: '', zoneName: '', description: '', status: 'Active' },
    ],
  };

  // Validation schema using Yup
  const validationSchema = Yup.object({
    zones: Yup.array().of(
      Yup.object().shape({
        zoneId: Yup.string().required('Zone ID is required'),
        zoneName: Yup.string().required('Zone Name is required'),
        description: Yup.string().required('Description is required'),
        status: Yup.string().required('Status is required'),
      })
    ),
  });

  // Handle form submission
  const onSubmit = (values, { setSubmitting, resetForm }) => {
    console.log('Form values:', values);
    setSubmitting(false);
    // Add API call or other logic here
    resetForm();
  };

  return (
   <div>
 <div>
 <div>Distribute Applications to Zone</div>
 <div>Get all student details regarding fee payment, transport, pocket money, other fee heads</div>
 </div>
 <div className='d-flex'>
    <button>Zone</button>
    <button>DGM</button>
    <button>Campus</button>
 </div>
 </div>
  );
};

export default ZoneApplication;
