import React from 'react';
import { Formik, Form, Field, ErrorMessage, FieldArray } from 'formik';
import {
  Box,
  TextField,
  Button,
  Typography,
  Container,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import { Delete as DeleteIcon } from '@mui/icons-material';
import * as Yup from 'yup';

const ZoneApplication = () => {
  // Initial form values including an array for multiple zones
  const initialValues = {
    zones: [
      { zoneName: '', description: '', status: 'Active' },
    ],
  };

  // Validation schema using Yup
  const validationSchema = Yup.object({
    zones: Yup.array().of(
      Yup.object().shape({
        zoneName: Yup.string().required('Zone Name is required'),
        description: Yup.string().required('Description is required'),
        status: Yup.string().required('Status is required'),
      })
    ),
  });

  // Handle form submission
  const onSubmit = (values, { setSubmitting, resetForm }) => {
    console.log('Form values:', values);
    setSubmitting(false);
    // Add API call or other logic here
    resetForm();
  };

  return (
    <Container maxWidth="lg">
      <Box
        sx={{
          mt: 4,
          p: 3,
          bgcolor: '#fff',
          borderRadius: 2,
          boxShadow: 1,
        }}
      >
        <Typography variant="h4" gutterBottom align="center">
          Zone Application
        </Typography>
        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={onSubmit}
        >
          {({ values, isSubmitting, setFieldValue }) => (
            <Form>
              <FieldArray name="zones">
                {({ push, remove }) => (
                  <div>
                    {values.zones.map((zone, index) => (
                      <Box key={index} sx={{ mb: 3, p: 2, border: '1px solid #ddd', borderRadius: 1 }}>
                        <Typography variant="h6">Zone {index + 1}</Typography>
                        <Field
                          as={TextField}
                          name={`zones[${index}].zoneName`}
                          label="Zone Name"
                          variant="outlined"
                          fullWidth
                          margin="normal"
                          helperText={<ErrorMessage name={`zones[${index}].zoneName`} />}
                          error={!!ErrorMessage.name}
                        />
                        <Field
                          as={TextField}
                          name={`zones[${index}].description`}
                          label="Description"
                          variant="outlined"
                          fullWidth
                          margin="normal"
                          multiline
                          rows={3}
                          helperText={<ErrorMessage name={`zones[${index}].description`} />}
                          error={!!ErrorMessage.name}
                        />
                        <FormControl fullWidth margin="normal">
                          <InputLabel>Status</InputLabel>
                          <Field
                            as={Select}
                            name={`zones[${index}].status`}
                            label="Status"
                            variant="outlined"
                            error={!!ErrorMessage.name}
                          >
                            <MenuItem value="Active">Active</MenuItem>
                            <MenuItem value="Inactive">Inactive</MenuItem>
                          </Field>
                          <ErrorMessage name={`zones[${index}].status`} />
                        </FormControl>
                        {index > 0 && (
                          <IconButton
                            color="error"
                            onClick={() => remove(index)}
                            sx={{ mt: 1 }}
                          >
                            <DeleteIcon />
                          </IconButton>
                        )}
                      </Box>
                    ))}
                    <Button
                      variant="outlined"
                      color="primary"
                      onClick={() => push({ zoneName: '', description: '', status: 'Active' })}
                      sx={{ mt: 2 }}
                    >
                      Add New Zone
                    </Button>
                  </div>
                )}
              </FieldArray>
              <Button
                type="submit"
                variant="contained"
                color="primary"
                disabled={isSubmitting}
                fullWidth
                sx={{ mt: 3 }}
              >
                Submit
              </Button>
              <Button
                variant="outlined"
                color="secondary"
                onClick={() => setFieldValue('zones', initialValues.zones)}
                fullWidth
                sx={{ mt: 2 }}
              >
                Reset
              </Button>
            </Form>
          )}
        </Formik>

        {/* Simulated Zone Data Table */}
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6" gutterBottom>
            Zone Data
          </Typography>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Zone Name</TableCell>
                <TableCell>Description</TableCell>
                <TableCell>Status</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {[
                { zoneName: 'Zone A', description: 'Main Zone', status: 'Active' },
                { zoneName: 'Zone B', description: 'Secondary Zone', status: 'Inactive' },
              ].map((zone, index) => (
                <TableRow key={index}>
                  <TableCell>{zone.zoneName}</TableCell>
                  <TableCell>{zone.description}</TableCell>
                  <TableCell>{zone.status}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
      </Box>
    </Container>
  );
};

export default ZoneApplication;
