import React from 'react';
import { Outlet } from 'react-router-dom';
import ApplicationModule from './ApplicationModule';

const ApplicationLayout = () => {
  return (

    <div className="d-flex flex-column"  style={{
        backgroundImage: `radial-gradient(rgba(255, 255, 255, 0.6) 20%, rgba(255, 255, 255, 0) 100%), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        maxHeight: '29%', // 29% of 92vh
        height: '100%', // Ensure it takes full available height up to max-height
        minHeight: '0', // Prevent collapse if parent height is constrained
        border: '1px solid red', // Temporary border to debug height
      }}>
      {/* Render ApplicationModule (header, search bar, navigation) */}
      <ApplicationModule />
      {/* Render sub-route content below */}
      <div style={{ flexGrow: 1, padding: '16px' }}>
        <Outlet />
      </div>
    </div>

  );
};

export default ApplicationLayout;
