import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import backgroundImage from '../Assets/BG.png';
import './ApplicationModule.css';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { TextField, IconButton, Typography, Button } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

const ApplicationModule = () => {
  const [searchValue, setSearchValue] = useState('');
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [filterValues, setFilterValues] = useState({
    academicYear: '',
    surname: '',
    studentName: '',
    parentName: '',
  });
  const location = useLocation();
  const searchBarRef = useRef(null);

  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
  };

  const handleFilterChange = (field) => (e) => {
    setFilterValues({ ...filterValues, [field]: e.target.value });
  };

  const handleFilterToggle = () => {
    setIsFilterOpen(!isFilterOpen);
  };

  // Update filter position on mount and window resize
  useEffect(() => {
    const updatePosition = () => {
      if (searchBarRef.current && isFilterOpen) {
        const searchBarRect = searchBarRef.current.getBoundingClientRect();
        const leftPosition = searchBarRect.left + window.scrollX - 30; // Move left by 10px
        const filterPanel = document.querySelector('.filter-panel');
        if (filterPanel) {
          filterPanel.style.left = `${leftPosition}px`;
          filterPanel.style.width = `${searchBarRect.width}px`;
        }
      }
    };

    updatePosition();
    window.addEventListener('resize', updatePosition);
    return () => window.removeEventListener('resize', updatePosition);
  }, [isFilterOpen]);

  const getActiveLink = (path) => (location.pathname === path ? 'active' : '');

  return (
    <div
      className="d-flex flex-column"
      style={{
        backgroundImage: `radial-gradient(rgba(255, 255, 255, 0.6) 20%, rgba(255, 255, 255, 0) 100%), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        height: '90%',
        position: 'relative',
      }}
    >
      {/* Header Section */}
      <div className="px-3 pt-2 pb-1" style={{ flexShrink: 0 }}>
        <div style={{ fontSize: '36px', fontWeight: 700, color: '#252C32' }}>
          Application Module
        </div>
        <div style={{ color: '#6E7C87', fontSize: '16px', fontWeight: 400 }}>
          Access and manage comprehensive student details seamlessly. View personalized profiles tailored to your campus.
        </div>
      </div>

      {/* Content Section */}
      <div className="d-flex flex-column flex-grow-1">
        {/* Search Bar with Filter Toggle */}
        <div className="px-3 mt-2" style={{ flexShrink: 0 }}>
          <Box
            ref={searchBarRef}
            sx={{
              width: 400,
              backgroundColor: '#FFFFFF',
              height: '5vh',
              borderTopLeftRadius: '7px',
              borderTopRightRadius: '7px',
              borderBottomLeftRadius: '0',
              borderBottomRightRadius: '0',
              display: 'flex',
              alignItems: 'center',
              border: '1px solid #EBECEF',
              padding: '0 8px',
            }}
          >
            <InputAdornment position="start" className="ms-2">
              <IconButton onClick={handleFilterToggle} edge="start">
                <SearchIcon />
              </IconButton>
            </InputAdornment>
            <input
              type="text"
              placeholder="Search with application no"
              value={searchValue}
              onChange={handleSearchChange}
              style={{
                border: 'none',
                outline: 'none',
                fontSize: '14px',
                fontWeight: 400,
                backgroundColor: 'transparent',
                width: '100%',
                color: '#9C9C9C',
              }}
            />
          </Box>
        </div>

        {/* Filter Panel Below Search Bar */}
        {isFilterOpen && (
          <div
            className="filter-panel"
            style={{
              backgroundColor: '#FFFFFF',
              border: '1px solid #EBECEF',
              borderTop: 'none',
              borderBottomLeftRadius: '7px',
              borderBottomRightRadius: '7px',
              padding: '20px',
              marginTop: '-1px',
              boxSizing: 'border-box',
              position: 'absolute',
              top: '136px',
              zIndex: 1000,
            }}
          >
            <Typography variant="h6" style={{ color: '#252C32', marginBottom: '20px' }}>
              Filter Options
            </Typography>
            <TextField
              fullWidth
              label="Academic Year"
              value={filterValues.academicYear}
              onChange={handleFilterChange('academicYear')}
              variant="outlined"
              margin="normal"
              InputProps={{ style: { fontSize: '14px', color: '#9C9C9C' } }}
              InputLabelProps={{ style: { fontSize: '14px' } }}
            />
            <TextField
              fullWidth
              label="Surname"
              value={filterValues.surname}
              onChange={handleFilterChange('surname')}
              variant="outlined"
              margin="normal"
              InputProps={{ style: { fontSize: '14px', color: '#9C9C9C' } }}
              InputLabelProps={{ style: { fontSize: '14px' } }}
            />
            <TextField
              fullWidth
              label="Student Name"
              value={filterValues.studentName}
              onChange={handleFilterChange('studentName')}
              variant="outlined"
              margin="normal"
              InputProps={{ style: { fontSize: '14px', color: '#9C9C9C' } }}
              InputLabelProps={{ style: { fontSize: '14px' } }}
            />
            <TextField
              fullWidth
              label="Parent Name"
              value={filterValues.parentName}
              onChange={handleFilterChange('parentName')}
              variant="outlined"
              margin="normal"
              InputProps={{ style: { fontSize: '14px', color: '#9C9C9C' } }}
              InputLabelProps={{ style: { fontSize: '14px' } }}
            />
            <Button
              variant="contained"
              style={{ marginTop: '20px', backgroundColor: '#3425FF', textTransform: 'none' }}
              onClick={handleFilterToggle}
            >
              Apply Filters
            </Button>
          </div>
        )}

        {/* Navigation Bar */}
        <div
          className="px-3 mt-3"
          style={{
            flexShrink: 0,
            overflowY: 'auto',
          }}
        >
          <nav className="custom-nav">
            <Link
              className={`nav-link ${getActiveLink('/application/analytics')}`}
              to="/application/analytics"
            >
              Analytics
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/zone-application')}`}
              to="/application/zone-application"
              style={{ display: 'flex', alignItems: 'center' }}
            >
              Distribute
              <KeyboardArrowDownIcon />
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/application-status')}`}
              to="/application/application-status"
            >
              Application Status
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/receipts')}`}
              to="/application/receipts"
            >
              Receipts
            </Link>
          </nav>
        </div>
      </div>
    </div>
  );
};

export default ApplicationModule;
