import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import backgroundImage from '../Assets/BG.png';
import './ApplicationModule.css';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { Drawer, TextField, Select, MenuItem, Button, IconButton } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search'; // Import Search icon
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';

const ApplicationModule = () => {
  const [searchValue, setSearchValue] = useState('');
  const [filterOpen, setFilterOpen] = useState(false); // State to toggle filter sidebar
  const location = useLocation();

  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
    // Add search logic here (e.g., filter data based on searchValue)
  };

  const handleFilterToggle = () => {
    setFilterOpen(!filterOpen);
  };

  // Determine the active nav link based on the current path
  const getActiveLink = (path) => (location.pathname === path ? 'active' : '');

  return (
    <div
      className="d-flex flex-column"
      style={{
        backgroundImage: `radial-gradient(rgba(255, 255, 255, 0.6) 20%, rgba(255, 255, 255, 0) 100%), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        height: '90%',
      }}
    >
      {/* Header Section */}
      <div className="px-3 pt-2 pb-1" style={{ flexShrink: 0 }}>
        <div style={{ fontSize: '36px', fontWeight: 700, color: '#252C32' }}>
          Application Module
        </div>
        <div style={{ color: '#6E7C87', fontSize: '16px', fontWeight: 400 }}>
          Access and manage comprehensive student details seamlessly. View personalized profiles tailored to your campus.
        </div>
      </div>

      {/* Content Section */}
      <div className="d-flex flex-column flex-grow-1">
        {/* Search Bar with Filter Icon */}
        <div className="px-3 mt-2" style={{ flexShrink: 0 }}>
          <Box
            sx={{
              width: 400,
              backgroundColor: '#FFFFFF',
              height: '5vh',
              borderTopLeftRadius: '7px',
              borderTopRightRadius: '7px',
              borderBottomLeftRadius: '0',
              borderBottomRightRadius: '0',
              display: 'flex',
              alignItems: 'center',
              border: '1px solid #EBECEF',
              padding: '0 8px',
            }}
          >
            <InputAdornment position="start" className="ms-2">
              <IconButton onClick={handleFilterToggle} edge="start">
                <SearchIcon />
              </IconButton>
            </InputAdornment>
            <input
              type="text"
              placeholder="Search with application no"
              value={searchValue}
              onChange={handleSearchChange}
              style={{
                border: 'none',
                outline: 'none',
                fontSize: '14px',
                fontWeight: 400,
                backgroundColor: 'transparent',
                width: '100%',
                color: '#9C9C9C',
              }}
            />
          </Box>
        </div>

        {/* Navigation Bar */}
        <div
          className="px-3 mt-3"
          style={{
            flexShrink: 0,
            overflowY: 'auto',
          }}
        >
          <nav className="custom-nav">
            <Link
              className={`nav-link ${getActiveLink('/application/analytics')}`}
              to="/application/analytics"
            >
              Analytics
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/zone-application')}`}
              to="/application/zone-application"
              style={{ display: 'flex', alignItems: 'center' }}
            >
              Distribute
              <KeyboardArrowDownIcon />
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/application-status')}`}
              to="/application/application-status"
            >
              Application Status
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/receipts')}`}
              to="/application/receipts"
            >
              Receipts
            </Link>
          </nav>
        </div>
      </div>

      {/* Filter Sidebar */}
      <Drawer
        anchor="right"
        open={filterOpen}
        onClose={handleFilterToggle}
        PaperProps={{
          sx: {
            width: 300, // Sidebar-like width
            backgroundColor: '#FFFFFF',
            borderTopLeftRadius: '7px',
            borderBottomLeftRadius: '7px',
            padding: '16px',
          },
        }}
      >
        <LocalizationProvider dateAdapter={AdapterDateFns}>
          <div>
            <TextField
              fullWidth
              label="Application No"
              variant="outlined"
              sx={{ mb: 2 }}
            />
            <Select
              fullWidth
              label="Campus"
              variant="outlined"
              displayEmpty
              sx={{ mb: 2 }}
            >
              <MenuItem value="" disabled>
                Select Campus
              </MenuItem>
              <MenuItem value="campus1">Campus 1</MenuItem>
              <MenuItem value="campus2">Campus 2</MenuItem>
            </Select>
            <Select
              fullWidth
              label="Zone"
              variant="outlined"
              displayEmpty
              sx={{ mb: 2 }}
            >
              <MenuItem value="" disabled>
                Select Zone
              </MenuItem>
              <MenuItem value="zone1">Zone 1</MenuItem>
              <MenuItem value="zone2">Zone 2</MenuItem>
            </Select>
            <DatePicker
              label="Date Range"
              renderInput={(params) => <TextField {...params} fullWidth sx={{ mb: 2 }} />}
            />
            <Button
              variant="contained"
              color="primary"
              fullWidth
              onClick={handleFilterToggle}
              sx={{ mt: 2 }}
            >
              Apply Filter
            </Button>
          </div>
        </LocalizationProvider>
      </Drawer>
    </div>
  );
};

export default ApplicationModule;
