import React, { useState } from 'react';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import backgroundImage from '../Assets/BG.png'; // Verify path
import TextField from '@mui/material/TextField';

const ApplicationModule = () => {
  const [searchValue, setSearchValue] = useState('');

  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
    // Add search logic here (e.g., filter data based on searchValue)
  };

  return (
    <div
      className="d-flex flex-column justify-content-between"
      style={{
        backgroundImage: `radial-gradient(rgba(255, 255, 255, 0.6) 20%, rgba(255, 255, 255, 0) 100%), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        minHeight: '100%', // Stretch to fill parent
        height: '100%', // Ensure full height of <main>
      }}
    >
      <div>
        <div
          className="px-3 pt-2"
          style={{ fontSize: '36px', fontWeight: 700, color: '#252C32' }}
        >
          Application Module
        </div>

        <div
          className="px-3"
          style={{ color: '#6E7C87', fontSize: '16px', fontWeight: 400 }}
        >
          Access and manage comprehensive student details seamlessly. View personalized profiles tailored to your campus.
        </div>
      </div>

      <div>
        <div className="px-3">
          <Box
            sx={{
              width: 400,
              backgroundColor: '#FFFFFF',
              height: '5vh',
              display: 'flex',
              alignItems: 'center',
              border: '1px solid #EBECEF',
              padding: '0 8px',
            }}
          >
            <TextField
              placeholder="Search with application no"
              value={searchValue}
              onChange={handleSearchChange}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start" className="ms-2">
                    <svg>...</svg>
                  </InputAdornment>
                ),
                disableUnderline: true,
              }}
              sx={{
                width: '100%',
                '& .MuiInputBase-root': {
                  borderTopLeftRadius: '50%',
                  borderTopRightRadius: '50%',
                  borderBottomLeftRadius: '10px',
                  borderBottomRightRadius: '10px',
                  backgroundColor: 'transparent',
                },
                '& .MuiInputBase-input': {
                  fontSize: '16px',
                  fontWeight: 400,
                  color: '#000000',
                  padding: 0,
                },
                '& .MuiInputBase-input::placeholder': {
                  color: '#00000040',
                  opacity: 1,
                },
              }}
            />
          </Box>
        </div>

        {/* Structured placeholder content */}
        <div className="px-3 py-2" >
          Analytics Distribute Application Status Receipts
        </div>

      </div>
    </div>
  );
};

export default ApplicationModule;
