import React from 'react';
import { Formik, Form, Field, ErrorMessage, FieldArray } from 'formik';
import {
  Box,
  TextField,
  Button,
  Typography,
  Container,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Grid,
} from '@mui/material';
import { Delete as DeleteIcon } from '@mui/icons-material';
import * as Yup from 'yup';

const ZoneApplication = () => {
  // Initial form values including an array for multiple zones
  const initialValues = {
    zones: [
      { zoneId: '', zoneName: '', description: '', status: 'Active' },
    ],
  };

  // Validation schema using Yup
  const validationSchema = Yup.object({
    zones: Yup.array().of(
      Yup.object().shape({
        zoneId: Yup.string().required('Zone ID is required'),
        zoneName: Yup.string().required('Zone Name is required'),
        description: Yup.string().required('Description is required'),
        status: Yup.string().required('Status is required'),
      })
    ),
  });

  // Handle form submission
  const onSubmit = (values, { setSubmitting, resetForm }) => {
    console.log('Form values:', values);
    setSubmitting(false);
    // Add API call or other logic here
    resetForm();
  };

  return (
    <Container maxWidth="lg">
      <Box
        sx={{
          mt: 4,
          p: 3,
          bgcolor: '#fff',
          borderRadius: 2,
          boxShadow: 1,
        }}
      >
        <Typography variant="h4" gutterBottom align="center" color="primary">
          Zone Application
        </Typography>
        <Formik
          initialValues={initialValues}
          validationSchema={validationSchema}
          onSubmit={onSubmit}
        >
          {({ values, isSubmitting, setFieldValue }) => (
            <Form>
              <FieldArray name="zones">
                {({ push, remove }) => (
                  <div>
                    {values.zones.map((zone, index) => (
                      <Box
                        key={index}
                        sx={{
                          mb: 4,
                          p: 3,
                          border: '1px solid #e0e0e0',
                          borderRadius: 2,
                          bgcolor: '#f9f9f9',
                        }}
                      >
                        <Grid container spacing={2}>
                          <Grid item xs={12}>
                            <Typography variant="h6" gutterBottom>
                              Zone {index + 1}
                            </Typography>
                          </Grid>
                          <Grid item xs={12} sm={4}>
                            <Field
                              as={TextField}
                              name={`zones[${index}].zoneId`}
                              label="Zone ID"
                              variant="outlined"
                              fullWidth
                              margin="normal"
                              helperText={<ErrorMessage name={`zones[${index}].zoneId`} />}
                              error={!!ErrorMessage.name}
                            />
                          </Grid>
                          <Grid item xs={12} sm={4}>
                            <Field
                              as={TextField}
                              name={`zones[${index}].zoneName`}
                              label="Zone Name"
                              variant="outlined"
                              fullWidth
                              margin="normal"
                              helperText={<ErrorMessage name={`zones[${index}].zoneName`} />}
                              error={!!ErrorMessage.name}
                            />
                          </Grid>
                          <Grid item xs={12} sm={4}>
                            <FormControl fullWidth margin="normal">
                              <InputLabel>Status</InputLabel>
                              <Field
                                as={Select}
                                name={`zones[${index}].status`}
                                label="Status"
                                variant="outlined"
                                error={!!ErrorMessage.name}
                              >
                                <MenuItem value="Active">Active</MenuItem>
                                <MenuItem value="Inactive">Inactive</MenuItem>
                              </Field>
                              <ErrorMessage name={`zones[${index}].status`} />
                            </FormControl>
                          </Grid>
                          <Grid item xs={12}>
                            <Field
                              as={TextField}
                              name={`zones[${index}].description`}
                              label="Description"
                              variant="outlined"
                              fullWidth
                              margin="normal"
                              multiline
                              rows={3}
                              helperText={<ErrorMessage name={`zones[${index}].description`} />}
                              error={!!ErrorMessage.name}
                            />
                          </Grid>
                          {index > 0 && (
                            <Grid item xs={12} sx={{ textAlign: 'right' }}>
                              <IconButton
                                color="error"
                                onClick={() => remove(index)}
                                sx={{ mt: 1 }}
                              >
                                <DeleteIcon />
                              </IconButton>
                            </Grid>
                          )}
                        </Grid>
                      </Box>
                    ))}
                    <Button
                      variant="outlined"
                      color="primary"
                      onClick={() => push({ zoneId: '', zoneName: '', description: '', status: 'Active' })}
                      sx={{ mt: 2 }}
                    >
                      Add New Zone
                    </Button>
                  </div>
                )}
              </FieldArray>
              <Box sx={{ mt: 4, display: 'flex', gap: 2 }}>
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  disabled={isSubmitting}
                  sx={{ flex: 1 }}
                >
                  Submit
                </Button>
                <Button
                  variant="outlined"
                  color="secondary"
                  onClick={() => setFieldValue('zones', initialValues.zones)}
                  sx={{ flex: 1 }}
                >
                  Reset
                </Button>
              </Box>
            </Form>
          )}
        </Formik>

        {/* Zone Data Table */}
        <Box sx={{ mt: 6 }}>
          <Typography variant="h6" gutterBottom>
            Zone Data
          </Typography>
          <Table sx={{ mt: 2 }}>
            <TableHead>
              <TableRow>
                <TableCell sx={{ fontWeight: 'bold' }}>Zone ID</TableCell>
                <TableCell sx={{ fontWeight: 'bold' }}>Zone Name</TableCell>
                <TableCell sx={{ fontWeight: 'bold' }}>Description</TableCell>
                <TableCell sx={{ fontWeight: 'bold' }}>Status</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {[
                { zoneId: 'Z001', zoneName: 'Zone A', description: 'Main Zone', status: 'Active' },
                { zoneId: 'Z002', zoneName: 'Zone B', description: 'Secondary Zone', status: 'Inactive' },
              ].map((zone, index) => (
                <TableRow key={index}>
                  <TableCell>{zone.zoneId}</TableCell>
                  <TableCell>{zone.zoneName}</TableCell>
                  <TableCell>{zone.description}</TableCell>
                  <TableCell>{zone.status}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Box>
      </Box>
    </Container>
  );
};

export default ZoneApplication;
