import React, { useState } from 'react';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import backgroundImage from '../Assets/BG.png'; // Verify path

const ApplicationModule = () => {
  const [searchValue, setSearchValue] = useState('');

  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
    // Add search logic here (e.g., filter data based on searchValue)
  };

  return (
  <>  <div
      className="d-flex flex-column justify-content-between"
      style={{
        backgroundImage: `radial-gradient(rgba(255, 255, 255, 0.6) 20%, rgba(255, 255, 255, 0) 100%), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      
      }}
    >
      <div>
        <div
          className="px-3 pt-2"
          style={{ fontSize: '36px', fontWeight: 700, color: '#252C32' }}
        >
          Application Module
        </div>

        <div
          className="px-3"
          style={{ color: '#6E7C87', fontSize: '16px', fontWeight: 400 }}
        >
          Access and manage comprehensive student details seamlessly. View personalized profiles tailored to your campus.
        </div>
      </div>

      <div>
        <div className="px-3">
          <Box
            sx={{
              width: 400,
              backgroundColor: '#FFFFFF',
              height: '5vh',
              borderRadius: '10px',
              display: 'flex',
              alignItems: 'center',
              border: '1px solid #EBECEF',
              padding: '0 8px',
            }}
          >
            <InputAdornment position="start" className="ms-2">
              <svg
                width="17"
                height="17"
                viewBox="0 0 17 17"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M7.79167 13.4583C10.9213 13.4583 13.4583 10.9213 13.4583 7.79167C13.4583 4.66205 10.9213 2.125 7.79167 2.125C4.66205 2.125 2.125 4.66205 2.125 7.79167C2.125 10.9213 4.66205 13.4583 7.79167 13.4583Z"
                  stroke="#0A0A0A"
                  strokeWidth="1.41667"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
                <path
                  d="M14.8748 14.8751L11.829 11.8292"
                  stroke="#0A0A0A"
                  strokeWidth="1.41667"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
              </svg>
            </InputAdornment>
            <input
              type="text"
              placeholder="Search with application no"
              value={searchValue}
              onChange={handleSearchChange}
              style={{
                border: 'none',
                outline: 'none',
                fontSize: '16px',
                fontWeight: 400,
                backgroundColor: 'transparent',
                width: '100%',
                color: '#000000',
                '::placeholder': {
                  // Note: Inline ::placeholder may not work in all browsers; prefer CSS
                  color: '#00000040',
                },
              }}
            />
          </Box>
        </div>

        {/* Placeholder content; consider structuring or replacing */}
        <div>Analytics Distribute Application Status Receipts</div>
        <div>Analytics Distribute Application Status Receipts</div>
        <div> 
       
      </div>
    </div>
    </div>
    </>
  );
};

export default ApplicationModule;
