import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import Box from '@mui/material/Box';
import InputAdornment from '@mui/material/InputAdornment';
import backgroundImage from '../Assets/BG.png';
import './ApplicationModule.css';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { TextField, IconButton, Select, MenuItem } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

const ApplicationModule = () => {
  const [searchValue, setSearchValue] = useState('');
  const [filterField, setFilterField] = useState(''); // State for selected filter field
  const location = useLocation();

  const handleSearchChange = (e) => {
    setSearchValue(e.target.value);
    // Add search logic here (e.g., filter data based on searchValue and filterField)
  };

  const handleFilterChange = (e) => {
    setFilterField(e.target.value);
    // Reset search value when changing filter if needed
    if (e.target.value !== filterField) setSearchValue('');
    // Add filter logic here if needed
  };

  // Determine the active nav link based on the current path
  const getActiveLink = (path) => (location.pathname === path ? 'active' : '');

  return (
    <div
      className="d-flex flex-column"
      style={{
        backgroundImage: `radial-gradient(rgba(255, 255, 255, 0.6) 20%, rgba(255, 255, 255, 0) 100%), url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        height: '90%',
      }}
    >
      {/* Header Section */}
      <div className="px-3 pt-2 pb-1" style={{ flexShrink: 0 }}>
        <div style={{ fontSize: '36px', fontWeight: 700, color: '#252C32' }}>
          Application Module
        </div>
        <div style={{ color: '#6E7C87', fontSize: '16px', fontWeight: 400 }}>
          Access and manage comprehensive student details seamlessly. View personalized profiles tailored to your campus.
        </div>
      </div>

      {/* Content Section */}
      <div className="d-flex flex-column flex-grow-1">
        {/* Search Bar with Dropdown Filter */}
        <div className="px-3 mt-2" style={{ flexShrink: 0 }}>
          <Box
            sx={{
              width: 400,
              backgroundColor: '#FFFFFF',
              height: '5vh',
              borderRadius: '7px', // Changed to full radius since no separate dropdown
              display: 'flex',
              alignItems: 'center',
              border: '1px solid #EBECEF',
              padding: '0 8px',
            }}
          >
            <InputAdornment position="start" className="ms-2">
              <IconButton edge="start">
                <SearchIcon />
              </IconButton>
            </InputAdornment>
            <Select
              value={filterField}
              onChange={handleFilterChange}
              displayEmpty
              IconComponent={KeyboardArrowDownIcon}
              inputProps={{ 'aria-label': 'filter' }}
              sx={{
                height: '100%',
                width: '30%', // Space for dropdown
                '& .MuiSelect-select': {
                  padding: '0 8px',
                  fontSize: '14px',
                  color: '#9C9C9C',
                },
                '& .MuiOutlinedInput-notchedOutline': {
                  border: 'none',
                },
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  border: 'none',
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  border: 'none',
                },
              }}
            >
              <MenuItem value="" disabled>
                Filter By
              </MenuItem>
              <MenuItem value="academicYear">Academic Year</MenuItem>
              <MenuItem value="surname">Surname</MenuItem>
              <MenuItem value="studentName">Student Name</MenuItem>
              <MenuItem value="parentName">Parent Name</MenuItem>
            </Select>
            <input
              type="text"
              placeholder={`Search with ${filterField || 'application no'}`}
              value={searchValue}
              onChange={handleSearchChange}
              style={{
                border: 'none',
                outline: 'none',
                fontSize: '14px',
                fontWeight: 400,
                backgroundColor: 'transparent',
                width: '70%', // Adjusted for dropdown
                color: '#9C9C9C',
              }}
            />
          </Box>
        </div>

        {/* Navigation Bar */}
        <div
          className="px-3 mt-3"
          style={{
            flexShrink: 0,
            overflowY: 'auto',
          }}
        >
          <nav className="custom-nav">
            <Link
              className={`nav-link ${getActiveLink('/application/analytics')}`}
              to="/application/analytics"
            >
              Analytics
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/zone-application')}`}
              to="/application/zone-application"
              style={{ display: 'flex', alignItems: 'center' }}
            >
              Distribute
              <KeyboardArrowDownIcon />
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/application-status')}`}
              to="/application/application-status"
            >
              Application Status
            </Link>
            <Link
              className={`nav-link ${getActiveLink('/application/receipts')}`}
              to="/application/receipts"
            >
              Receipts
            </Link>
          </nav>
        </div>
      </div>
    </div>
  );
};

export default ApplicationModule;
